import React from 'react';
import KdImageBox from './KdImageBox';
import KdTypography from './KdTypography';
import KdpaGrid from './KdpaGrid';

const ImageBoxWithLogo = ({ imageSrc, imageAlt, logoSrc, logoAlt, title }) => {
  return (
    <KdpaGrid container>
      <KdpaGrid item xs={12} sm={6} md={4}>
        <KdImageBox src={imageSrc} alt={imageAlt} modal={true} quality="HIGH" loading="lazy" />
      </KdpaGrid>
      <KdpaGrid item xs={12} sm={6} md={8}>
        <KdImageBox src={logoSrc} alt={logoAlt} modal={false} quality="LOW" loading="lazy" />
        <KdTypography variant="h6" title={title} />
      </KdpaGrid>
    </KdpaGrid>
  );
};

export default ImageBoxWithLogo;

